const opmlContent = $input.first().json.OPML;

// Use regex to match all RSS outline elements
const rssPattern = /<outline[^>]*type="rss"[^>]*text="([^"]*)"[^>]*xmlUrl="([^"]*)"[^>]*\/>/g;
const podcasts = [];

let match;
while ((match = rssPattern.exec(opmlContent)) !== null) {
  podcasts.push({
    name: match[1],
    rssUrl: match[2]
  });
}

return podcasts.map(podcast => ({
  json: {
    name: podcast.name,
    rssUrl: podcast.rssUrl
  }
}));
