const podcastUUID = $('Filter for your podcast').first().json.myPodcast.trim();
const chartResults = $('Get Daily Top 200 on Apple Podcasts').first().json.podcasts;

let rank = null;
let foundPodcast = null;

for (let i = 0; i < chartResults.length; i++) {
  if (chartResults[i].uuid.trim() === podcastUUID) {
    rank = i + 1;
    foundPodcast = chartResults[i];
    break;
  }
}

return [{
  json: {
    date: new Date().toISOString().split('T')[0],
    rank: rank || "Not in Top Charts",
    podcastName: foundPodcast?.name || "Unknown",
    country: $('Get Daily Top 200 on Apple Podcasts').first().json.country,
    genres: $('Get Daily Top 200 on Apple Podcasts').first().json.genres
  }
}];
